    private void jBSendActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBSendActionPerformed
        String login = txtLoginF.getText();
        String senha = txtSenhaF.getText();
        
        ArrayFuncionario repoFuncionario = GerenciadorLivraria.desserializarFunc(DiretorioArquivos.getDir() + "\\src\\projeto\\sistema\\arquivos\\funcionarios.bin");
        // Se existir
        if (repoFuncionario.existeUsuario(login)) {
            // Cheque a senha
            if (repoFuncionario.buscarUsuario(login).getSenha().equals(senha)) {
            AreaFuncionario af = new AreaFuncionario();
                af.setVisible(true); 
            }
            else {
            AvisoSenhaErrada asf = new AvisoSenhaErrada();
                asf.setVisible(true);  
            }
        }
        // nao existe
        else {
            UsuarioInexistente ui = new UsuarioInexistente();
                ui.setVisible(true);  
        }
    }//GEN-LAST:event_jBSendActionPerformed

